(function(){"use strict";var js_version="0.0.1";var js_debug=1;var watcher1,watcher2;var sidebar_mo=new MutationObserver(function(mutations){mutations.forEach(function(mutation){if(mutation.attributeName=="style"){updateQuickSearch()}})});if(true){var portlet=""+'<h2 class="boxTitle">Portlet</h2>'+'<div class="boxContent"></div>'+""}if(true){var action_css=""+" ";var css=action_css+""+""}function _debug(txt){if(js_debug){var d=new Date;var now=[d.getHours(),d.getMinutes(),d.getSeconds(),d.getMilliseconds()].join(":");console.log(now+": "+txt)}}function insertCss(css,css_id){var style=document.createElement("STYLE");if(css_id){style.id=css_id}style.innerHTML=css;document.querySelector("head").appendChild(style)}function updateCss(css,css_id){var styles=document.querySelector("#"+css_id);if(styles){styles.parentNode.removeChild(styles)}insertCss(css,css_id)}function addPortletUsersOnline(){var users=document.querySelector("section.box[data-box-identifier='com.woltlab.wcf.UsersOnline']");if(users){var sidebar=document.querySelector(".sidebar.boxesSidebarRight .boxContainer");if(sidebar){var box=document.createElement("SECTION");box.className="box";box.innerHTML=users.innerHTML;sidebar.prepend(box)}}}function startScript(){_debug("the-hive-tools started");addPortletUsersOnline()}startScript()})();